import React, { Component } from 'react';
import TOC from "./components/TOC";
import Content from "./components/Content";
import Subject from "./components/Subject";
import Aside from "./components/Aside";
import './App.css';

class App extends Component {
  // state값 초기화, 그 값으로 title="WEB" sub="world wide web"에 들어가는 값을 셋팅
  //Component가 실행될때 render함수 보다 먼저 실행되서 component를 초기화 시켜주고 싶은 코드를 constructor안에다가 코드를 작성
  //subject의 값을 state화 시킴
  constructor(props){
    super(props);
    this.state = {
      mode:'welcome',
      subject:{title:'WEB', sub:'World Wide Web!'},
      welcom:{title:'Welcome', desc:'Hello, React!!'},
      // content 배열에 담겨있는 것을 TOC에 담음
      contents:[
        {id:1, title:'HTML', desc:'HTML is for information'},
        {id:2, title:'CSS', desc:'CSS is form design'},
        {id:3, title:'JavaScript', desc:'HTML is for interactive'}
      ],
      explain:{title:'HTML', desc:'HTML is Hypertext Markup Language.'}
    }
  }
  render() {
    console.log('App render');
    var _title, _desc = null;
    if(this.state.mode === 'welcome'){
      _title = this.state.welcome.title;
      _desc = this.state.welcome.read;
    }else if(this.state.mode === 'read'){
      _title = this.state.contents[0].title;
      _desc = this.state.contents[0].desc;
    }
    return (
      <div className="App">
        <Subject title={this.state.subject.title} 
        sub={this.state.subject.sub}></Subject>
        <TOC data={this.state.contents}></TOC>
        <Content title={_title} desc={_desc}></Content>
        <Aside></Aside>
      </div>
    );
  }

}
export default App;
